#include<bits/stdc++.h>

using namespace std;
const int MAXN = 3e5 + 5;
long long x, y, z, dp[2][MAXN];
string s;
int main(){
  cin >> x >> y >> z >> s;
  int len = s.size();
  s = ' ' + s;
  for (int i = 1; i <= len; i++){
    bool capslook = s[i] == 'A';
    dp[capslook][i] = min({dp[capslook][i - 1] + x, dp[!capslook][i - 1] + y, dp[!capslook][i - 1] + x + z}); 
    capslook = !capslook;
    dp[capslook][i] = min({dp[capslook][i - 1] + x, dp[!capslook][i - 1] + y, dp[!capslook][i - 1] + x + z}); 
  }
  cout << dp[0][len];
  return 0;
}
